        </el-row>\r\n        <el-table :data=\"${subclassName}List\" :row-class-name=\"row${subClassName}Index\" @selection-change=\"handle${subClassName}SelectionChange\" ref=\"${subclassName}\">\r\n          <el-table-column type=\"selection\" width=\"50\" align=\"center\" />\r\n          <el-table-column label=\"序号\" align=\"center\" prop=\"index\" width=\"50\"/>\r\n#foreach($column in $subTable.columns)\r\n#set($javaField=$column.javaField)\r\n#set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\r\n#if($parentheseIndex != -1)\r\n#set($comment=$column.columnComment.substring(0, $parentheseIndex))\r\n#else\r\n#set($comment=$column.columnComment)\r\n#end\r\n#if($column.pk || $javaField == ${subTableFkclassName})\r\n#elseif($column.list && \"\" != $javaField)\r\n          <el-table-column label=\"$comment\" prop=\"${javaField}\">\r\n            <template slot-scope=\"scope\">\r\n              <el-input v-model=\"scope.row.$javaField\" placeholder=\"请输入$comment\" />\r\n            </template>\r\n          </el-table-column>\r\n#end\r\n#end\r\n        </el-table>\r\n#end\r\n      </el-form>\r\n      <div slot=\"footer\" class=\"dialog-footer\">\r\n        <el-button type=\"primary\" @click=\"submitForm\">确 定</el-button>\r\n        <el-button @click=\"cancel\">取 消</el-button>\r\n      </div>\r\n    </el-dialog>\r\n  </div>\r\n</template>\r\n\r\n<script>\r\n    import {\r\n        add${BusinessName},\r\n        del${BusinessName},\r\n        get${BusinessName},\r\n        list${BusinessName},\r\n        update${BusinessName}\r\n    } from \"@/api/${moduleName}/${businessName}\";\r\n    import ImageUpload from \'@/components/ImageUpload\';\r\n    import FileUpload from \'@/components/FileUpload\';\r\n    import Editor from \'@/components/Editor\';\r\n        #foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"imageUpload\")\r\n#break\r\n#end\r\n#end\r\n#foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"fileUpload\")\r\n#break\r\n#end\r\n#end\r\n#foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"editor\")\r\n#break\r\n#end\r\n#end\r\n\r\nexport default {\r\n  name: \"${BusinessName}\",\r\n  components: {\r\n#foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"imageUpload\")\r\n    ImageUpload,\r\n#break\r\n#end\r\n#end\r\n#foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"fileUpload\")\r\n    FileUpload,\r\n#break\r\n#end\r\n#end\r\n#foreach($column in $columns)\r\n#if($column.insert && !$column.superColumn && !$column.pk && $column.htmlType == \"editor\")\r\n    Editor,\r\n#break\r\n#end\r\n#end\r\n  },\r\n  data() {\r\n    return {\r\n      // 遮罩层\r\n      loading: true,\r\n      // 选中数组\r\n      ids: [],\r\n#if($table.sub)\r\n      // 子表选中数据\r\n      checked${subClassName}: [],\r\n#end\r\n      // 非单个禁用\r\n      single: true,\r\n      // 非多个禁用\r\n      multiple: true,\r\n      // 显示搜索条件\r\n      showSearch: true,\r\n      // 总条数\r\n      total: 0,\r\n      // ${functionName}表格数据\r\n      ${businessName}List: [],\r\n#if($table.sub)\r\n      // ${subTable.functionName}表格数据\r\n      ${subclassName}List: [],\r\n#end\r\n      // 弹出层标题\r\n      title: \"\",\r\n      // 是否显示弹出层\r\n      open: false,\r\n#foreach ($column in $columns)\r\n#set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\r\n#if($parentheseIndex != -1)\r\n#set($comment=$column.columnComment.substring(0, $parentheseIndex))\r\n#else\r\n#set($comment=$column.columnComment)\r\n#end\r\n#if(${column.dictType} != \'\')\r\n      // $comment字典\r\n      ${column.javaField}Options: [],\r\n#elseif($column.htmlType == \"datetime\" && $column.queryType == \"BETWEEN\")\r\n#set($AttrName=$column.javaField.substring(0,1).toUpperCase() + ${column.javaField.substring(1)})\r\n      // $comment时间范围\r\n      daterange${AttrName}: [],\r\n#end\r\n#end\r\n      // 查询参数\r\n      queryParams: {\r\n        pageNum: 1,\r\n        pageSize: 10,\r\n#foreach ($column in $columns)\r\n#if($column.query)\r\n        $column.javaField: null#if($velocityCount != $columns.size()),#end\r\n\r\n#end\r\n#end\r\n      },\r\n      // 表单参数\r\n      form: {},\r\n      // 表单校验\r\n      rules: {\r\n#foreach ($column in $columns)\r\n#if($column.required)\r\n#set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\r\n#if($parentheseIndex != -1)\r\n#set($comment=$column.columnComment.substring(0, $parentheseIndex))\r\n#else\r\n#set($comment=$column.columnComment)\r\n#end\r\n        $column.javaField: [\r\n          { required: true, message: \"$comment不能为空\", trigger: #if($column.htmlType == \"select\")\"change\"#else\"blur\"#end }\r\n        ]#if($velocityCount != $columns.size()),#end\r\n\r\n#end\r\n#end\r\n      }\r\n    };\r\n  },\r\n  created() {\r\n    this.getList();\r\n#foreach ($column in $columns)\r\n#if(${column.dictType} != \'\')\r\n    this.getDicts(\"${column.dictType}\").then(response => {\r\n      this.${column.javaField}Options = response.data;\r\n    });\r\n#end\r\n#end\r\n  },\r\n  methods: {\r\n    /** 查询${functionName}列表 */\r\n    getList() {\r\n      this.loading = true;\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"datetime\" && $column.queryType == \"BETWEEN\")\r\n      this.queryParams.params = {};\r\n#break\r\n#end\r\n#end\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"datetime\" && $column.queryType == \"BETWEEN\")\r\n#set($AttrName=$column.javaField.substring(0,1).toUpperCase() + ${column.javaField.substring(1)})\r\n      if (null != this.daterange${AttrName} && \'\' != this.daterange${AttrName}) {\r\n        this.queryParams.params[\"begin${AttrName}\"] = this.daterange${AttrName}[0];\r\n        this.queryParams.params[\"end${AttrName}\"] = this.daterange${AttrName}[1];\r\n      }\r\n#end\r\n#end\r\n      list${BusinessName}(this.queryParams).then(response => {\r\n        this.${businessName}List = response.rows;\r\n        this.total = response.total;\r\n        this.loading = false;\r\n      });\r\n    },\r\n#foreach ($column in $columns)\r\n#if(${column.dictType} != \'\')\r\n#set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\r\n#if($parentheseIndex != -1)\r\n#set($comment=$column.columnComment.substring(0, $parentheseIndex))\r\n#else\r\n#set($comment=$column.columnComment)\r\n#end\r\n    // $comment字典翻译\r\n    ${column.javaField}Format(row, column) {\r\n      return this.selectDictLabel#if($column.htmlType == \"checkbox\")s#end(this.${column.javaField}Options, row.${column.javaField});\r\n    },\r\n#end\r\n#end\r\n    // 取消按钮\r\n    cancel() {\r\n      this.open = false;\r\n      this.reset();\r\n    },\r\n    // 表单重置\r\n    reset() {\r\n      this.form = {\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"radio\")\r\n        $column.javaField: #if($column.javaType == \"Integer\" || $column.javaType == \"Long\")0#else\"0\"#end#if($velocityCount != $columns.size()),#end\r\n\r\n#elseif($column.htmlType == \"checkbox\")\r\n        $column.javaField: []#if($velocityCount != $columns.size()),#end\r\n\r\n#else\r\n        $column.javaField: null#if($velocityCount != $columns.size()),#end\r\n\r\n#end\r\n#end\r\n      };\r\n#if($table.sub)\r\n      this.${subclassName}List = [];\r\n#end\r\n      this.resetForm(\"form\");\r\n    },\r\n    /** 搜索按钮操作 */\r\n    handleQuery() {\r\n      this.queryParams.pageNum = 1;\r\n      this.getList();\r\n    },\r\n    /** 重置按钮操作 */\r\n    resetQuery() {\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"datetime\" && $column.queryType == \"BETWEEN\")\r\n#set($AttrName=$column.javaField.substring(0,1).toUpperCase() + ${column.javaField.substring(1)})\r\n      this.daterange${AttrName} = [];\r\n#end\r\n#end\r\n      this.resetForm(\"queryForm\");\r\n      this.handleQuery();\r\n    },\r\n    // 多选框选中数据\r\n    handleSelectionChange(selection) {\r\n      this.ids = selection.map(item => item.${pkColumn.javaField})\r\n      this.single = selection.length!==1\r\n      this.multiple = !selection.length\r\n    },\r\n    /** 新增按钮操作 */\r\n    handleAdd() {\r\n      this.reset();\r\n      this.open = true;\r\n      this.title = \"添加${functionName}\";\r\n    },\r\n    /** 修改按钮操作 */\r\n    handleUpdate(row) {\r\n      this.reset();\r\n      const ${pkColumn.javaField} = row.${pkColumn.javaField} || this.ids\r\n      get${BusinessName}(${pkColumn.javaField}).then(response => {\r\n        this.form = response.data;\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"checkbox\")\r\n        this.form.$column.javaField = this.form.${column.javaField}.split(\",\");\r\n#end\r\n#end\r\n#if($table.sub)\r\n        this.${subclassName}List = response.data.${subclassName}List;\r\n#end\r\n        this.open = true;\r\n        this.title = \"修改${functionName}\";\r\n      });\r\n    },\r\n    /** 提交按钮 */\r\n    submitForm() {\r\n      this.#[[$]]#refs[\"form\"].validate(valid => {\r\n        if (valid) {\r\n#foreach ($column in $columns)\r\n#if($column.htmlType == \"checkbox\")\r\n          this.form.$column.javaField = this.form.${column.javaField}.join(\",\");\r\n#end\r\n#end\r\n#if($table.sub)\r\n          this.form.${subclassName}List = this.${subclassName}List;\r\n#end\r\n          if (this.form.${pkColumn.javaField} != null) {\r\n            update${BusinessName}(this.form).then(response => {\r\n              this.msgSuccess(\"修改成功\");\r\n              this.open = false;\r\n              this.getList();\r\n            });\r\n          } else {\r\n            add${BusinessName}(this.form).then(response => {\r\n              this.msgSuccess(\"新增成功\");\r\n              this.open = false;\r\n              this.getList();\r\n            });\r\n          }\r\n        }\r\n      });\r\n    },\r\n    /** 删除按钮操作 */\r\n    handleDelete(row) {\r\n      const ${pkColumn.javaField}s = row.${pkColumn.javaField} || this.ids;\r\n      this.$confirm(\'是否确认删除${functionName}编号为\"\' + ${pkColumn.javaField}s + \'\"的数据项?\', \"警告\", {\r\n          confirmButtonText: \"确定\",\r\n          cancelButtonText: \"取消\",\r\n          type: \"warning\"\r\n        }).then(function() {\r\n          return del${BusinessName}(${pkColumn.javaField}s);\r\n        }).then(() => {\r\n          this.getList();\r\n          this.msgSuccess(\"删除成功\");\r\n        })\r\n    },\r\n#if($table.sub)\r\n	/** ${subTable.functionName}序号 */\r\n    row${subClassName}Index({ row, rowIndex }) {\r\n      row.index = rowIndex + 1;\r\n    },\r\n    /** ${subTable.functionName}添加按钮操作 */\r\n    handleAdd${subClassName}() {\r\n      let obj = {};\r\n#foreach($column in $subTable.columns)\r\n#if($column.pk || $column.javaField == ${subTableFkclassName})\r\n#elseif($column.list && \"\" != $javaField)\r\n      obj.$column.javaField = \"\";\r\n#end\r\n#end\r\n      this.${subclassName}List.push(obj);\r\n    },\r\n    /** ${subTable.functionName}删除按钮操作 */\r\n    handleDelete${subClassName}() {\r\n      if (this.checked${subClassName}.length == 0) {\r\n        this.$alert(\"请先选择要删除的${subTable.functionName}数据\", \"提示\", { confirmButtonText: \"确定\", });\r\n      } else {\r\n        this.${subclassName}List.splice(this.checked${subClassName}[0].index - 1, 1);\r\n      }\r\n    },\r\n    /** 单选框选中数据 */\r\n    handle${subClassName}SelectionChange(selection) {\r\n      if (selection.length > 1) {\r\n        this.$refs.${subclassName}.clearSelection();\r\n        this.$refs.${subclassName}.toggleRowSelection(selection.pop());\r\n      } else {\r\n        this.checked${subClassName} = selection;\r\n      }\r\n    },\r\n#end\r\n    /** 导出按钮操作 */\r\n    handleExport() {\r\n      this.download(\'${moduleName}/${businessName}/export\', {\r\n        ...this.queryParams\r\n      }, `${moduleName}_${businessName}.xlsx`)\r\n    }\r\n  }\r\n};\r\n</script>', 'java', 'private', '1.0.0', 'index.vue.vm', 'vue/views/#{moduleName}/#{businessName}/index.vue', NULL, '2022-09-04 15:18:47', '2022-09-04 16:24:24', 17, 17, '隔壁老于', 0);
INSERT INTO `template_management` VALUES (14, '模板属性说明', '## 模板变量信息说明 --\r\n## ${tplCategory} 生成的类型\r\n## ${tableName} 数据表名称\r\n## ${functionName} 程序名称\r\n## ${ClassName} 类名-首字母大写\r\n## ${className} 类名-首字母小写\r\n## ${moduleName} 模块名称\r\n## ${BusinessName} 业务名称 -首字母大写\r\n## ${businessName} 业务名称 -首字母小写\r\n## ${basePackage} 包前缀 如 xxx.xx.x 的 xxx\r\n## ${packageName} 包路径\r\n## ${author} 作者\r\n## ${datetime} 当前时间-模板运行时的时间\r\n## ${pkColumn} 主键字段\r\n## ${importList} 需要导入包的列表\r\n## 如果字段类型包含 LOCAL_DATE_TIME 则导入 java.time.LocalDateTime com.fasterxml.jackson.annotation.JsonFormat\r\n## 如果所有列包含BigDecimal 则导入java.math.BigDecimal\r\n## 如果生成的类型是是数据结构则导入 java.util.List\r\n## ${permissionPrefix} 更具模块名及业务名自动生成的权限字符   moduleName：businessName：\r\n## ${columns} 表的所有字段集合\r\n## ${isQuery} 是否生成更新代码\r\n## ${isInsert} 是否生成插入代码\r\n## ${isUpdate} 是否生成更新代码\r\n## ${isRemove} 是否生成删除代码\r\n## ${isWrapper} 是否生成Wrapper代码\r\n## ${isManager} 是否生成manager层级\r\n\r\n## ${table} GenTable 表对象属性说明\r\n\r\n##  编号  tableId\r\n##  表名称  tableName\r\n##  表描述  tableComment\r\n##  关联父表的表名  subTableName\r\n##  本表关联父表的外键名  subTableFkName\r\n##  实体类名称(首字母大写) className\r\n##  生成的类型（crud单表操作 tree树表操作 sub主子表操作） tplCategory\r\n##  生成包路径  packageName\r\n##  生成模块名 moduleName\r\n##  生成业务名  businessName\r\n##  生成功能名  functionName\r\n##  作者  functionAuthor\r\n##  生成代码方式（0zip压缩包 1自定义路径）  genType\r\n##  生成路径（不填默认项目路径） genPath\r\n##  主键信息  类型 GenTableColumn  pkColumn\r\n##  子表信息  类型 GenTable subTable \r\n##  表列信息 List<GenTableColumn> columns\r\n##  其它生成选项  options\r\n##  树编码字段  treeCode\r\n##  树父编码字段  treeParentCode\r\n##  树名称字段  treeName\r\n##  上级菜单ID字段 parentMenuId\r\n##  级菜单名称字段 parentMenuName\r\n##  是否生成查询 isQuery\r\n##  是否生成插入  isInsert\r\n##  是否生成更新  isUpdate\r\n##  是否生成删除  isRemove\r\n##  是否生成Lambda条件语句 isWrapper\r\n##  是否生成Manager层 isManager\r\n##  使用的模板id  templateIds\r\n\r\n## ${table.pkColumn} GenTableColumn 主键信息属性说明\r\n\r\n ##     编号 columnId\r\n ##    归属表编号 tableId\r\n ##    列名称  columnName\r\n ##    列描述  columnComment\r\n ##    列类型  columnType\r\n ##    JAVA类型   javaType\r\n ##    JAVA字段名  javaField\r\n ##    是否主键（1是） isPk\r\n ##    是否必填（1是） isRequired\r\n ##    是否为插入字段（1是）  isInsert\r\n ##    是否编辑字段（1是）   isEdit\r\n ##    是否列表字段（1是）   isList\r\n ##    是否查询字段（1是）   isQuery\r\n ##    查询方式 queryType（EQ等于、NE不等于、GT大于、LT小于、LIKE模糊、BETWEEN范围）  \r\n ##    显示类型 htmlType（input文本框、textarea文本域、select下拉框、checkbox复选框、radio单选框、datetime日期控件、image图片上传控件、upload文件上传控件、editor富文本控件）\r\n ##    字典类型 dictType\r\n ##    排序   sort', 'java', 'private', '1.0.0', 'demo.vm', '/', NULL, '2022-09-04 15:18:47', '2022-09-04 16:24:24', 17, 17, '隔壁老于', 0);
INSERT INTO `template_management` VALUES (15, '数据库文档', '<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"yes\"?>\r\n<?mso-application progid=\"Word.Document\"?>\r\n<pkg:package xmlns:pkg=\"http://schemas.microsoft.com/office/2006/xmlPackage\">\r\n    <pkg:part pkg:name=\"/_rels/.rels\" pkg:contentType=\"application/vnd.openxmlformats-package.relationships+xml\"\r\n              pkg:padding=\"512\">\r\n        <pkg:xmlData>\r\n            <Relationships xmlns=\"http://schemas.openxmlformats.org/package/2006/relationships\">\r\n                <Relationship Id=\"rId3\"\r\n                              Type=\"http://schemas.openxmlformats.org/officeDocument/2006/relationships/extended-properties\"\r\n                              Target=\"docProps/app.xml\"/>\r\n                <Relationship Id=\"rId2\"\r\n                              Type=\"http://schemas.openxmlformats.org/package/2006/relationships/metadata/core-properties\"\r\n                              Target=\"docProps/core.xml\"/>\r\n                <Relationship Id=\"rId1\"\r\n                              Type=\"http://schemas.openxmlformats.org/officeDocument/2006/relationships/officeDocument\"\r\n                              Target=\"word/document.xml\"/>\r\n            </Relationships>\r\n        </pkg:xmlData>\r\n    </pkg:part>\r\n    <pkg:part pkg:name=\"/word/document.xml\"\r\n              pkg:contentType=\"application/vnd.openxmlformats-officedocument.wordprocessingml.document.main+xml\">\r\n        <pkg:xmlData>\r\n            <w:document xmlns:wpc=\"http://schemas.microsoft.com/office/word/2010/wordprocessingCanvas\"\r\n                        xmlns:cx=\"http://schemas.microsoft.com/office/drawing/2014/chartex\"\r\n                        xmlns:cx1=\"http://schemas.microsoft.com/office/drawing/2015/9/8/chartex\"\r\n                        xmlns:cx2=\"http://schemas.microsoft.com/office/drawing/2015/10/21/chartex\"\r\n                        xmlns:cx3=\"http://schemas.microsoft.com/office/d