-- authorization_code definition
CREATE TABLE `authorization_code` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键', `code` varchar(64) DEFAULT NULL COMMENT '授权码', `state` int(11) DEFAULT 0 COMMENT '状态 0未使用 1已使用 2.可重复使用 3.废弃', `config` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '配置信息', `description` varchar(255) DEFAULT NULL COMMENT '描述', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `del_flag` int(11) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`id`) USING BTREE, KEY `index_code` (`code`) USING BTREE, KEY `index_code_state` (`code`, `state`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='授权码';
-- oauth_client_details definition
CREATE TABLE `oauth_client_details` ( `client_id` varchar(64) NOT NULL COMMENT '客户端id', `client_name` varchar(12) NOT NULL COMMENT '客户端名称', `client_secret` varchar(64) DEFAULT NULL COMMENT '客户端密钥', `scope` text DEFAULT NULL COMMENT '客户端可申请的权限', `authorized_grant_types` varchar(255) DEFAULT NULL COMMENT '支持的授权模式:authorization_code,password,implicit,client_credentials', `redirect_uri` text DEFAULT NULL COMMENT '重定向URI 逗号分隔 *代表不限制', `access_token_validity` int(11) DEFAULT NULL COMMENT 'access_token的有效时间(秒)', `refresh_token_validity` int(11) DEFAULT NULL COMMENT '刷新token的有效时间(秒)', `client_token_validity` int(11) DEFAULT NULL COMMENT '客户端token的有效时间(秒)', `additional_information` text DEFAULT NULL COMMENT '拓展数据', `status` int(1) NOT NULL DEFAULT 0 COMMENT '状态（0正常 1停用）', `is_audit` int(1) DEFAULT 0 COMMENT '审核状态', `remark` varchar(500) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '备注', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间' ) ENGINE = InnoDB DEFAULT CHARSET = utf8 COMMENT ='授权客户端信息';
-- sys_dict_data definition
CREATE TABLE `sys_dict_data` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键', `dict_type_id` bigint(20) NOT NULL COMMENT '字典类型', `dict_sort` int(11) NOT NULL DEFAULT 0 COMMENT '字典排序', `dict_label` varchar(32) NOT NULL COMMENT '显示标签', `dict_key` varchar(100) NOT NULL DEFAULT '' COMMENT '字典标签', `dict_value` varchar(255) NOT NULL DEFAULT '' COMMENT '字典键值', `dict_value_type` varchar(6) DEFAULT NULL COMMENT '字典参数类型', `is_default` int(11) DEFAULT 0 COMMENT '是否默认（0否 1是）', `status` int(11) DEFAULT 0 COMMENT '状态（0正常 1停用）', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `remark` varchar(255) DEFAULT NULL COMMENT '备注', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='字典数据表';
-- sys_dict_type definition
CREATE TABLE `sys_dict_type` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '字典主键', `dict_name` varchar(32) NOT NULL DEFAULT '' COMMENT '字典名称', `dict_code` varchar(32) NOT NULL COMMENT '字典编码', `status` int(11) DEFAULT 0 COMMENT '状态（0正常 1停用）', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `remark` varchar(255) DEFAULT NULL COMMENT '备注', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='字典类型表';
-- sys_log definition
CREATE TABLE `sys_log` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键', `data` longtext DEFAULT NULL COMMENT '内容', `create_time` datetime DEFAULT NULL COMMENT '创建时间', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='系统日志';
-- sys_notice definition
CREATE TABLE `sys_notice` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '公告ID', `notice_title` varchar(64) NOT NULL COMMENT '公告标题', `notice_type` char(1) NOT NULL DEFAULT '1' COMMENT '公告类型（1 通知 2公告 3活动）', `notice_content` longblob DEFAULT NULL COMMENT '公告内容', `status` char(1) DEFAULT '0' COMMENT '公告状态（0正常 1关闭）', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `remark` varchar(255) DEFAULT NULL COMMENT '备注', `del_flag` int(11) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='通知公告表';
-- sys_resource definition
CREATE TABLE `sys_resource` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'ID', `parent_id` bigint(20) DEFAULT 0 COMMENT '父级ID', `resource_name` varchar(50) NOT NULL COMMENT '资源名称', `resource_code` varchar(100) NOT NULL COMMENT '资源编码', `resource_path` varchar(2048) NOT NULL COMMENT '资源id路径，以/开始以/结束，从根节点ID到当点节点ID，节点ID之间以/连接', `resource_type` char(1) DEFAULT '' COMMENT '资源类型（M菜单 F功能）', `order_number` int(11) DEFAULT 0 COMMENT '显示顺序', `url` varchar(200) DEFAULT '' COMMENT '路径', `icon` text DEFAULT NULL COMMENT '图标', `icon_type` tinyint(1) NOT NULL DEFAULT 0 COMMENT '图标类型：0-组件(默认) 1-URL 2-图片', `status` tinyint(1) DEFAULT 0 COMMENT '状态（0正常 1停用）', `permissions` text DEFAULT NULL COMMENT '权限编码集合', `auth_flag` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否需要授权访问：1-是、0-否', `route_mode` tinyint(1) NOT NULL DEFAULT 0 COMMENT '路由集成模式：0-hash（默认），1-browser', `embedded_type` tinyint(1) NOT NULL DEFAULT 0 COMMENT '嵌入方式：0-iFame（默认）、1-微前端、2-弹出新窗口、3-页面自导航', `sub_routes` text DEFAULT NULL COMMENT '内部子路由', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `resource_desc` varchar(500) DEFAULT '' COMMENT '备注', `del_flag` tinyint(1) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='系统资源';
-- sys_role definition
CREATE TABLE `sys_role` ( `role_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '角色ID', `role_name` varchar(30) NOT NULL COMMENT '角色名称', `role_key` varchar(100) NOT NULL COMMENT '角色权限字符串', `role_sort` int(11) NOT NULL DEFAULT 0 COMMENT '显示顺序', `is_default` int(11) NOT NULL DEFAULT 0 COMMENT '是否默认角色,0否 1是', `status` int(11) NOT NULL DEFAULT 0 COMMENT '角色状态（0正常 1停用）', `create_by` bigint(20) DEFAULT NULL COMMENT '创建者', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新者', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `remark` varchar(500) DEFAULT NULL COMMENT '备注', `del_flag` int(11) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`role_id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='角色信息表';
-- sys_role_resource definition
CREATE TABLE `sys_role_resource` ( `role_id` bigint(20) NOT NULL COMMENT '角色ID', `resource_id` bigint(20) NOT NULL COMMENT '资源ID' ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='角色和资源关联表';
-- sys_user_role definition
CREATE TABLE `sys_user_role` ( `user_id` bigint(20) NOT NULL COMMENT '用户ID', `role_id` bigint(20) NOT NULL COMMENT '角色ID', PRIMARY KEY (`user_id`, `role_id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='用户和角色关联表';
-- user_info definition
CREATE TABLE `user_info` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键', `union_id` varchar(64) DEFAULT NULL COMMENT 'unionId', `secret` varchar(64) NOT NULL COMMENT '用户密钥', `platform` varchar(32) DEFAULT NULL COMMENT '第三方平台', `nick_name` varchar(32) DEFAULT NULL COMMENT '用户昵称', `email` varchar(255) DEFAULT NULL COMMENT '邮箱', `avatar_url` varchar(255) DEFAULT NULL COMMENT '头像链接', `authorization` text DEFAULT NULL COMMENT '授权信息', `points` int(11) DEFAULT 0 COMMENT '积分', `state` int(11) DEFAULT 0 COMMENT '状态', `is_admin` int(11) DEFAULT 0 COMMENT '是否为管理员', `password` varchar(64) DEFAULT NULL COMMENT '密码', `salt` varchar(32) DEFAULT NULL COMMENT '盐', `login_ip` varchar(64) DEFAULT NULL COMMENT '登录ip', `last_login_time` datetime DEFAULT NULL COMMENT '最后登陆时间', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `update_by` bigint(20) DEFAULT NULL COMMENT '更新人', `del_flag` int(11) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`id`) USING BTREE, KEY `index_unid_platform` (`union_id`, `platform`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='用户信息';
-- user_message definition
CREATE TABLE `user_message` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键', `user_id` bigint(20) NOT NULL COMMENT '用户id', `title` varchar(255) NOT NULL COMMENT '标题', `type` int(11) NOT NULL DEFAULT 1 COMMENT '消息类型', `status` int(11) NOT NULL DEFAULT 0 COMMENT '状态：0未读，1已读', `content` longtext NOT NULL COMMENT '消息详情', `create_time` datetime NOT NULL DEFAULT current_timestamp() COMMENT '创建时间', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='用户消息';
-- user_points_log definition
CREATE TABLE `user_points_log` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键', `user_id` bigint(20) NOT NULL COMMENT '用户id', `points` int(11) NOT NULL COMMENT '分数', `current_points` int(11) NOT NULL COMMENT '当前积分', `description` varchar(255) NOT NULL COMMENT '描述', `create_time` datetime NOT NULL COMMENT '创建时间', `del_flag` int(11) DEFAULT 0 COMMENT '删除标记', PRIMARY KEY (`id`) USING BTREE ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT ='用户积分记录';

