-- blacklist definition
CREATE TABLE IF NOT EXISTS `blacklist` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键', `ip` varchar(255) DEFAULT NULL COMMENT 'IP', `disable_time` datetime DEFAULT NULL COMMENT '黑名单结束时间', `create_time` datetime DEFAULT NULL COMMENT '创建时间', `update_time` datetime DEFAULT NULL COMMENT '更新时间', `del_flag` tinyint(1) DEFAULT 0 COMMENT '是否删除(1未删除；0已删除)', PRIMARY KEY (`id`) USING BTREE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='IP黑名单';
-- sys_log definition
CREATE TABLE IF NOT EXISTS `sys_log` ( `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键', `data` longtext DEFAULT NULL COMMENT '内容', `create_time` datetime DEFAULT NULL COMMENT '创建时间', PRIMARY KEY (`id`) USING BTREE ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='系统日志';
